mapper.update:
UPDATE smbms_bill set billCode= #{billCode},productName=#{productName},
productUnit=#{productUnit},productCount=#{productCount},
totalPrice=#{totalPrice},providerId=#{providerId},isPaymen=#{isPaymen},modifyBy=#{modifyBy}
 where id=#{id}

mapper.select:
		SELECT *,p.proName from smbms_bill b,smbms_provider p where b.providerId =p.id
		
 			and b.productName like concat('%',#{productName},'%') 
	 	
	 	
	 		and b.providerId=#{providerId} 
	 	
	 	
	 		and b.isPaymen=#{isPaymen} 
	 	
	 	limit #{curPage},#{pageSize}

		SELECT count(1) from smbms_bill b,smbms_provider p where b.providerId =p.id
		
 			and b.productName like concat('%',#{productName},'%') 
	 	
	 	
	 		and b.providerId=#{providerId} 
	 	
	 	
	 		and b.isPaymen=#{isPaymen}

mapper.insert:
INSERT INTO `smbms_bill` VALUES (null, #{billCode}, 
#{productName}, null, #{productUnit}, #{productCount}, #{totalPrice},#{isPaymen}, #{createdBy},
 now(), null, null, #{providerId});
